mapper.select:
SELECT s.id, s.name, s.cat_id, s.dept_id, s.address, s.description, s.manager, s.booking_rule_id, s.creator, s.create_date, s.updater, s.update_date, c.name AS category, d.name AS dept_name, m.username AS manager_name, cr.username AS creator_name, u.username AS updater_name, b.id AS booking_rule_id, b.price, b.approval_required, i.id AS image_id, i.image_url, t.id AS tag_id, t.tag_name FROM svc_service s LEFT JOIN svc_category c ON s.cat_id = c.id LEFT JOIN sys_dept d ON s.dept_id = d.id LEFT JOIN svc_image i ON s.id = i.space_id LEFT JOIN svc_booking_rule b ON s.booking_rule_id = b.id LEFT JOIN svc_service_tag st ON s.id = st.space_id LEFT JOIN svc_tag t ON st.tag_id = t.id LEFT JOIN sys_user m ON s.manager = m.id LEFT JOIN sys_user cr ON s.creator = cr.id LEFT JOIN sys_user u ON s.updater = u.id
and s.name like #{name}
AND s.cat_id = #{catId}
AND st.tag_id = #{tagId}
AND s.dept_id IN
#{id}
